//\n//  OnboardingStep.swift\n//  SWY6\n//\n//  Created on 2025-10-21\n//\n\nimport Foundation\n\nenum OnboardingStep: Equatable, CaseIterable {\n    case culturalLanguage\n    case foodPreferences\n    case personalBackground\n    case appearanceStyle\n    case personality\n    case dateNight\n    case lifestyle\n    case sports\n    case hobbiesSkills\n    case politicalStance\n    case moviePreferences\n    case causes\n    case musicPreferences\n    case locationsTravel\n    case outdoorActivities\n    case goingOut\n    case stayingIn\n    case fanFavourites\n    case socialMedia\n    case gaming\n    case complete\n    \n    mutating func advance() {\n        guard let currentIndex = Self.allCases.firstIndex(of: self),\n              currentIndex < Self.allCases.count - 1 else {\n            self = .complete\n            return\n        }\n        self = Self.allCases[currentIndex + 1]\n    }\n    \n    func previous() -> OnboardingStep? {\n        guard let currentIndex = Self.allCases.firstIndex(of: self),\n              currentIndex > 0 else {\n            return nil\n        }\n        return Self.allCases[currentIndex - 1]\n    }\n}